import React, { useRef, useState, useEffect } from 'react';
import Webcam from 'react-webcam';
import axios from 'axios';
import './App.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

function App() {
  const webcamRef = useRef(null);
  const [capturedImage, setCapturedImage] = useState(null);
  const [detectedEmotion, setDetectedEmotion] = useState(null);
  const [emotionConfidence, setEmotionConfidence] = useState(null);
  const [products, setProducts] = useState([]);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [categories, setCategories] = useState(['All']);
  const [emotionHistory, setEmotionHistory] = useState([]);
  const [error, setError] = useState(null);
  const [allEmotions, setAllEmotions] = useState({});

  const emotionEmojis = {
    happy: 'üòä',
    sad: 'üò¢',
    angry: 'üò†',
    neutral: 'üòê',
    surprise: 'üò≤',
    fear: 'üò®',
    disgust: 'ü§¢'
  };

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = async () => {
    try {
      const response = await axios.get(`${API_URL}/categories`);
      console.log('Categories fetched:', response.data);
      setCategories(response.data.categories || ['All']);
    } catch (err) {
      console.error('Error fetching categories:', err);
      setCategories(['All']);
    }
  };

  const captureImage = () => {
    if (!webcamRef.current) {
      setError('Webcam not ready. Please allow camera access.');
      console.error('Webcam ref not available');
      return;
    }

    try {
      const imageSrc = webcamRef.current.getScreenshot();
      if (!imageSrc) {
        setError('Failed to capture image. Please try again.');
        console.error('Screenshot is null');
        return;
      }
      setCapturedImage(imageSrc);
      analyzeEmotion(imageSrc);
    } catch (err) {
      setError('Error capturing image: ' + err.message);
      console.error('Capture error:', err);
    }
  };

  const analyzeEmotion = async (base64Image) => {
    try {
      setIsAnalyzing(true);
      setError(null);

      console.log('Sending image to backend...');
      const response = await axios.post(`${API_URL}/detect-emotion`, {
        image: base64Image
      });

      console.log('Backend response received:', response.data);
      console.log('Products from backend:', response.data.products);

      const data = response.data;
      
      // Set emotion data
      setDetectedEmotion(data.emotion);
      setEmotionConfidence(data.confidence);
      setAllEmotions(data.all_emotions || {});

      // Set products - make sure it's an array
      const productsArray = Array.isArray(data.products) ? data.products : [];
      console.log('Setting products array:', productsArray);
      console.log('Number of products:', productsArray.length);
      
      setProducts(productsArray);
      setSelectedCategory('All');

      // Add to history
      setEmotionHistory([
        {
          emotion: data.emotion,
          timestamp: new Date().toLocaleTimeString(),
          confidence: data.confidence
        },
        ...emotionHistory
      ].slice(0, 5));

      setIsAnalyzing(false);
    } catch (err) {
      console.error('Error analyzing emotion:', err);
      console.error('Error details:', err.response?.data);
      setError(err.response?.data?.details || err.response?.data?.error || 'Failed to analyze emotion. Check backend.');
      setIsAnalyzing(false);
      setProducts([]);
    }
  };

  const handleDetectAgain = () => {
    console.log('Resetting emotion detection...');
    setDetectedEmotion(null);
    setEmotionConfidence(null);
    setCapturedImage(null);
    setProducts([]);
    setError(null);
    setSelectedCategory('All');
    setAllEmotions({});
  };

  const handleCategoryChange = (category) => {
    console.log('Category changed to:', category);
    setSelectedCategory(category);
  };

  // Filter products by category
  const filteredProducts = selectedCategory === 'All' 
    ? products 
    : products.filter(p => p.category === selectedCategory);

  console.log('Filtered products for display:', filteredProducts);

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-500 via-purple-500 to-pink-500 p-4 md:p-8">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="text-center mb-8 md:mb-12">
          <h1 className="text-4xl md:text-5xl font-bold text-white mb-2">
            üé≠ Emotion-Based Product Recommender
          </h1>
          <p className="text-lg md:text-xl text-white opacity-90">
            Discover products that match your current emotion
          </p>
        </div>

        {/* Error Message */}
        {error && (
          <div className="max-w-4xl mx-auto mb-6 bg-red-100 border-l-4 border-red-500 text-red-700 p-4 rounded">
            <p className="font-bold">Error:</p>
            <p>{error}</p>
          </div>
        )}

        {/* Main Layout */}
        <div className="grid md:grid-cols-3 gap-6">
          {/* Left Column - Webcam & Emotion */}
          <div className="md:col-span-1">
            <div className="bg-white rounded-lg shadow-xl p-6">
              <h2 className="text-2xl font-bold mb-4 text-gray-800">üì∑ Capture Your Emotion</h2>

              {!detectedEmotion ? (
                <>
                  <div className="bg-black rounded-lg overflow-hidden mb-4">
                    <Webcam
                      ref={webcamRef}
                      screenshotFormat="image/jpeg"
                      className="w-full"
                      videoConstraints={{
                        width: { ideal: 640 },
                        height: { ideal: 480 },
                        facingMode: 'user'
                      }}
                    />
                  </div>

                  <button
                    onClick={captureImage}
                    disabled={isAnalyzing}
                    className={`w-full py-3 rounded-lg font-bold text-white text-lg transition ${
                      isAnalyzing
                        ? 'bg-gray-400 cursor-not-allowed'
                        : 'bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 shadow-lg'
                    }`}
                  >
                    {isAnalyzing ? '‚è≥ Analyzing...' : 'üéØ Capture & Analyze'}
                  </button>
                </>
              ) : (
                <>
                  <div className="bg-gray-100 rounded-lg overflow-hidden mb-4">
                    <img src={capturedImage} alt="Captured" className="w-full" />
                  </div>

                  <div className="text-center mb-6 p-4 bg-gradient-to-r from-blue-50 to-purple-50 rounded-lg">
                    <div className="text-6xl mb-2">{emotionEmojis[detectedEmotion] || 'üòê'}</div>
                    <p className="text-2xl font-bold text-gray-800 capitalize mb-2">{detectedEmotion}</p>
                    <p className="text-lg font-semibold text-blue-600">{(emotionConfidence * 100).toFixed(0)}% Confidence</p>
                  </div>

                  <button
                    onClick={handleDetectAgain}
                    className="w-full py-3 rounded-lg font-bold text-white bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 transition"
                  >
                    üîÑ Detect Again
                  </button>
                </>
              )}

              {/* Emotion History */}
              {emotionHistory.length > 0 && (
                <div className="mt-6 pt-6 border-t">
                  <h3 className="text-lg font-bold text-gray-800 mb-3">üìä Recent</h3>
                  <div className="space-y-2">
                    {emotionHistory.map((item, idx) => (
                      <div key={idx} className="flex justify-between items-center p-2 bg-gray-100 rounded text-sm">
                        <span>{emotionEmojis[item.emotion]} {item.emotion}</span>
                        <span className="text-blue-600 font-semibold">{(item.confidence * 100).toFixed(0)}%</span>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </div>

          {/* Right Column - Products */}
          <div className="md:col-span-2">
            <div className="bg-white rounded-lg shadow-xl p-6">
              <h2 className="text-3xl font-bold mb-6 text-gray-800">üõçÔ∏è Recommended Products</h2>

              {/* Category Filter */}
              {detectedEmotion && categories.length > 0 && (
                <div className="mb-6 p-4 bg-gray-50 rounded-lg">
                  <label className="block text-sm font-bold text-gray-700 mb-3">Filter by Category:</label>
                  <div className="flex flex-wrap gap-2">
                    {categories.map((category) => (
                      <button
                        key={category}
                        onClick={() => handleCategoryChange(category)}
                        className={`px-4 py-2 rounded-full font-semibold transition ${
                          selectedCategory === category
                            ? 'bg-blue-600 text-white shadow-lg'
                            : 'bg-gray-200 text-gray-800 hover:bg-gray-300'
                        }`}
                      >
                        {category}
                      </button>
                    ))}
                  </div>
                </div>
              )}

              {/* Products Grid */}
              {filteredProducts && filteredProducts.length > 0 ? (
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {filteredProducts.map((product, idx) => {
                    console.log(`Rendering product ${idx}:`, product);
                    return (
                      <div
                        key={product.id || `product-${idx}`}
                        className="bg-gray-50 rounded-lg overflow-hidden shadow hover:shadow-lg transition"
                      >
                        <div className="relative h-40 bg-gray-300">
                          <img
                            src={product.image}
                            alt={product.name}
                            className="w-full h-full object-cover"
                            onError={(e) => {
                              e.target.src = 'https://via.placeholder.com/400x300?text=Product+Image';
                            }}
                          />
                        </div>
                        <div className="p-4">
                          <h3 className="font-bold text-gray-800 mb-2 line-clamp-2">{product.name}</h3>
                          <div className="flex justify-between items-start gap-2 mb-3">
                            <span className="text-2xl font-bold text-blue-600">{product.price}</span>
                            <span className="text-xs bg-purple-100 text-purple-800 px-2 py-1 rounded">
                              {product.category}
                            </span>
                          </div>
                          <button className="w-full py-2 bg-blue-600 text-white rounded-lg font-semibold hover:bg-blue-700 transition">
                            Add to Cart
                          </button>
                        </div>
                      </div>
                    );
                  })}
                </div>
              ) : detectedEmotion ? (
                <div className="text-center py-12 text-gray-500">
                  <p className="text-4xl mb-4">üòÖ</p>
                  <p className="text-lg">No products found for this category.</p>
                </div>
              ) : (
                <div className="text-center py-16 text-gray-500">
                  <p className="text-4xl mb-4">üëà</p>
                  <p className="text-lg font-semibold">Capture your emotion to see recommendations</p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
