import React, { useRef, useState, useEffect } from 'react';
import Webcam from 'react-webcam';
import axios from 'axios';
import './App.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

function App() {
  const webcamRef = useRef(null);
  const [capturedImage, setCapturedImage] = useState(null);
  const [detectedEmotion, setDetectedEmotion] = useState(null);
  const [emotionConfidence, setEmotionConfidence] = useState(null);
  const [products, setProducts] = useState([]);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [categories, setCategories] = useState(['All']);
  const [emotionHistory, setEmotionHistory] = useState([]);
  const [error, setError] = useState(null);

  const emotionEmojis = {
    happy: 'üòä',
    sad: 'üò¢',
    angry: 'üò†',
    neutral: 'üòê',
    surprise: 'üò≤',
    fear: 'üò®',
    disgust: 'ü§¢'
  };

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = async () => {
    try {
      const response = await axios.get(`${API_URL}/categories`);
      setCategories(response.data.categories);
    } catch (err) {
      console.error('Error fetching categories:', err);
    }
  };

  const captureImage = () => {
    if (!webcamRef.current) {
      setError('Webcam not ready. Please allow camera access.');
      return;
    }

    try {
      const imageSrc = webcamRef.current.getScreenshot();
      if (!imageSrc) {
        setError('Failed to capture image. Please try again.');
        return;
      }
      setCapturedImage(imageSrc);
      analyzeEmotion(imageSrc);
    } catch (err) {
      setError('Error capturing image: ' + err.message);
    }
  };

  const analyzeEmotion = async (base64Image) => {
    try {
      setIsAnalyzing(true);
      setError(null);

      console.log('Sending image to backend...');
      const response = await axios.post(`${API_URL}/detect-emotion`, {
        image: base64Image
      });

      console.log('Backend response:', response.data);

      const data = response.data;
      setDetectedEmotion(data.emotion);
      setEmotionConfidence(data.confidence);

      let filteredByCategory = data.products;
      if (selectedCategory !== 'All') {
        filteredByCategory = data.products.filter(
          (p) => p.category === selectedCategory
        );
      }

      setProducts(filteredByCategory);

      setEmotionHistory([
        {
          emotion: data.emotion,
          timestamp: new Date().toLocaleTimeString(),
          confidence: data.confidence
        },
        ...emotionHistory
      ].slice(0, 5));

      setIsAnalyzing(false);
    } catch (err) {
      console.error('Error analyzing emotion:', err);
      setError(err.response?.data?.error || 'Failed to analyze emotion. Check backend.');
      setIsAnalyzing(false);
    }
  };

  const handleDetectAgain = () => {
    setDetectedEmotion(null);
    setEmotionConfidence(null);
    setCapturedImage(null);
    setProducts([]);
    setError(null);
    setSelectedCategory('All');
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
    if (detectedEmotion && products.length > 0) {
      const filtered = products.filter((p) => category === 'All' || p.category === category);
      setProducts(filtered);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-500 via-purple-500 to-pink-500 p-4 md:p-8">
      <div className="max-w-7xl mx-auto">
        <div className="text-center mb-8 md:mb-12 fade-in">
          <h1 className="text-4xl md:text-5xl font-bold text-white mb-2">
            üé≠ Emotion-Based Product Recommender
          </h1>
          <p className="text-lg md:text-xl text-white opacity-90">
            Discover products that match your current emotion
          </p>
        </div>

        {error && (
          <div className="max-w-4xl mx-auto mb-6 bg-red-100 border border-red-400 text-red-700 px-4 py-4 rounded-lg fade-in">
            <strong>Error:</strong> {error}
            <button
              onClick={() => setError(null)}
              className="float-right font-bold text-xl hover:text-red-900"
            >
              √ó
            </button>
          </div>
        )}

        <div className="grid md:grid-cols-3 gap-6 md:gap-8">
          {/* Left Sidebar - Webcam */}
          <div className="md:col-span-1">
            <div className="bg-white rounded-lg shadow-xl p-6 fade-in">
              <h2 className="text-2xl font-bold mb-4 text-gray-800">üì∑ Capture Your Emotion</h2>

              {!detectedEmotion ? (
                <>
                  <div className="relative bg-black rounded-lg overflow-hidden mb-4">
                    <Webcam
                      ref={webcamRef}
                      screenshotFormat="image/jpeg"
                      className="w-full"
                      videoConstraints={{
                        width: { ideal: 640 },
                        height: { ideal: 480 },
                        facingMode: 'user'
                      }}
                    />
                    <div className="absolute bottom-2 right-2 text-white text-xs bg-black bg-opacity-50 px-2 py-1 rounded">
                      üì∏ Live Preview
                    </div>
                  </div>

                  <button
                    onClick={captureImage}
                    disabled={isAnalyzing}
                    className={`w-full py-3 rounded-lg font-bold text-white text-lg transition ${
                      isAnalyzing
                        ? 'bg-gray-400 cursor-not-allowed'
                        : 'bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 shadow-lg'
                    }`}
                  >
                    {isAnalyzing ? '‚è≥ Analyzing...' : 'üéØ Capture & Analyze'}
                  </button>
                </>
              ) : (
                <>
                  <div className="relative bg-gray-100 rounded-lg overflow-hidden mb-4">
                    <img src={capturedImage} alt="Captured" className="w-full" />
                  </div>

                  <div className="text-center mb-6 p-4 bg-gradient-to-r from-blue-50 to-purple-50 rounded-lg">
                    <div className="text-7xl mb-2">{emotionEmojis[detectedEmotion] || 'üòê'}</div>
                    <p className="text-3xl font-bold text-gray-800 capitalize mb-2">{detectedEmotion}</p>
                    <p className="text-lg font-semibold text-blue-600">{(emotionConfidence * 100).toFixed(0)}% Confidence</p>
                  </div>

                  <button
                    onClick={handleDetectAgain}
                    className="w-full py-3 rounded-lg font-bold text-white bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 transition"
                  >
                    üîÑ Detect Again
                  </button>
                </>
              )}

              {emotionHistory.length > 0 && (
                <div className="mt-6 pt-6 border-t">
                  <h3 className="text-xl font-bold text-gray-800 mb-4">üìä Recent Emotions</h3>
                  <div className="space-y-3">
                    {emotionHistory.map((item, idx) => (
                      <div key={idx} className="flex justify-between items-center p-2 bg-gray-50 rounded">
                        <span className="text-2xl mr-2">{emotionEmojis[item.emotion]}</span>
                        <div className="flex-1">
                          <p className="font-semibold text-gray-800 capitalize">{item.emotion}</p>
                          <p className="text-xs text-gray-500">{item.timestamp}</p>
                        </div>
                        <p className="text-sm font-bold text-blue-600">{(item.confidence * 100).toFixed(0)}%</p>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </div>

          {/* Right Content - Products */}
          <div className="md:col-span-2">
            <div className="bg-white rounded-lg shadow-xl p-6 fade-in">
              <h2 className="text-3xl font-bold mb-6 text-gray-800">üõçÔ∏è Recommended Products</h2>

              {detectedEmotion && (
                <div className="mb-6 p-4 bg-gray-50 rounded-lg">
                  <label className="block text-sm font-bold text-gray-700 mb-3">üè∑Ô∏è Filter by Category:</label>
                  <div className="flex flex-wrap gap-2">
                    {categories.map((category) => (
                      <button
                        key={category}
                        onClick={() => handleCategoryChange(category)}
                        className={`px-4 py-2 rounded-full font-semibold transition ${
                          selectedCategory === category
                            ? 'bg-gradient-to-r from-blue-600 to-purple-600 text-white shadow-lg'
                            : 'bg-white text-gray-800 border border-gray-300 hover:border-blue-500'
                        }`}
                      >
                        {category}
                      </button>
                    ))}
                  </div>
                </div>
              )}

              {products.length > 0 ? (
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {products.map((product, idx) => (
                    <div
                      key={product.id || idx}
                      className="product-card bg-gray-50 rounded-lg overflow-hidden shadow hover:shadow-lg transition fade-in"
                      style={{ animationDelay: `${idx * 50}ms` }}
                    >
                      <div className="relative h-48 bg-gray-200 overflow-hidden">
                        <img
                          src={product.image}
                          alt={product.name}
                          className="w-full h-full object-cover hover:scale-110 transition-transform"
                          onError={(e) => {
                            e.target.src = 'https://via.placeholder.com/400x300?text=Product';
                          }}
                        />
                      </div>
                      <div className="p-4">
                        <h3 className="font-bold text-gray-800 mb-2 line-clamp-2">{product.name}</h3>
                        <div className="flex justify-between items-start gap-2 mb-3">
                          <span className="text-2xl font-bold text-blue-600">{product.price}</span>
                          <span className="text-xs bg-purple-100 text-purple-800 px-2 py-1 rounded-full">
                            {product.category}
                          </span>
                        </div>
                        <button className="w-full py-2 bg-gradient-to-r from-blue-500 to-purple-500 text-white rounded-lg font-semibold hover:from-blue-600 hover:to-purple-600 transition">
                          Add to Cart
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              ) : detectedEmotion ? (
                <div className="text-center py-12">
                  <p className="text-2xl text-gray-400">üòÖ</p>
                  <p className="text-gray-500 text-lg">No products found for the selected category.</p>
                </div>
              ) : (
                <div className="text-center py-16">
                  <p className="text-4xl mb-4">üëà</p>
                  <p className="text-gray-500 text-lg font-semibold">
                    Capture your emotion to see personalized recommendations
                  </p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
