import React, { useRef, useState, useEffect } from 'react';
import Webcam from 'react-webcam';
import axios from 'axios';
import './App.css';
import WebcamCapture from './components/WebcamCapture';
import ProductGrid from './components/ProductGrid';
import EmotionDisplay from './components/EmotionDisplay';
import CategoryFilter from './components/CategoryFilter';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

function App() {
  const webcamRef = useRef(null);
  const [capturedImage, setCapturedImage] = useState(null);
  const [detectedEmotion, setDetectedEmotion] = useState(null);
  const [emotionConfidence, setEmotionConfidence] = useState(null);
  const [products, setProducts] = useState([]);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [categories, setCategories] = useState(['All']);
  const [emotionHistory, setEmotionHistory] = useState([]);
  const [error, setError] = useState(null);
  const [webcamReady, setWebcamReady] = useState(false);
  const [userId] = useState('user_' + Math.random().toString(36).substr(2, 9));

  const emotionEmojis = {
    happy: 'üòä',
    sad: 'üò¢',
    angry: 'üò†',
    neutral: 'üòê',
    surprise: 'üò≤',
    fear: 'üò®',
    disgust: 'ü§¢'
  };

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = async () => {
    try {
      const response = await axios.get(`${API_URL}/categories`);
      setCategories(response.data.categories);
    } catch (err) {
      console.error('Error fetching categories:', err);
    }
  };

  const captureImage = () => {
    if (!webcamRef.current) {
      setError('Webcam not ready. Please allow camera access.');
      return;
    }
    
    try {
      const imageSrc = webcamRef.current.getScreenshot();
      if (!imageSrc) {
        setError('Failed to capture image. Please try again.');
        return;
      }
      setCapturedImage(imageSrc);
      analyzeEmotion(imageSrc);
    } catch (err) {
      setError('Error capturing image: ' + err.message);
    }
  };

  const analyzeEmotion = async (base64Image) => {
    try {
      setIsAnalyzing(true);
      setError(null);

      const response = await axios.post(`${API_URL}/detect-emotion`, {
        image: base64Image,
        user_id: userId
      });

      const data = response.data;
      setDetectedEmotion(data.emotion);
      setEmotionConfidence(data.confidence);

      // Filter products by category if selected
      let filteredByCategory = data.products;
      if (selectedCategory !== 'All') {
        filteredByCategory = data.products.filter(
          (p) => p.category === selectedCategory
        );
      }

      setProducts(filteredByCategory);

      // Add to history
      setEmotionHistory([
        {
          emotion: data.emotion,
          timestamp: new Date().toLocaleTimeString(),
          confidence: data.confidence
        },
        ...emotionHistory
      ].slice(0, 5));

      setIsAnalyzing(false);
    } catch (err) {
      console.error('Error analyzing emotion:', err);
      setError(err.response?.data?.error || 'Failed to analyze emotion. Make sure the backend is running.');
      setIsAnalyzing(false);
    }
  };

  const handleDetectAgain = () => {
    setDetectedEmotion(null);
    setEmotionConfidence(null);
    setCapturedImage(null);
    setProducts([]);
    setError(null);
    setSelectedCategory('All');
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
    if (detectedEmotion && products.length > 0) {
      const filtered = products.filter((p) => category === 'All' || p.category === category);
      setProducts(filtered);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-500 via-purple-500 to-pink-500 p-4 md:p-8">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="text-center mb-8 md:mb-12 fade-in">
          <h1 className="text-4xl md:text-5xl font-bold text-white mb-2">
            üé≠ Emotion-Based Product Recommender
          </h1>
          <p className="text-lg md:text-xl text-white opacity-90">
            Discover products that match your current emotion
          </p>
        </div>

        {/* Error Alert */}
        {error && (
          <div className="max-w-4xl mx-auto mb-6 bg-red-100 border border-red-400 text-red-700 px-4 py-4 rounded-lg fade-in">
            <strong>Error:</strong> {error}
            <button
              onClick={() => setError(null)}
              className="float-right font-bold text-xl hover:text-red-900"
            >
              √ó
            </button>
          </div>
        )}

        {/* Main Content */}
        <div className="grid md:grid-cols-3 gap-6 md:gap-8">
          {/* Left Sidebar - Webcam & Emotion */}
          <div className="md:col-span-1">
            <WebcamCapture
              webcamRef={webcamRef}
              capturedImage={capturedImage}
              detectedEmotion={detectedEmotion}
              emotionConfidence={emotionConfidence}
              emotionEmojis={emotionEmojis}
              isAnalyzing={isAnalyzing}
              onCapture={captureImage}
              onDetectAgain={handleDetectAgain}
              onWebcamReady={() => setWebcamReady(true)}
            />

            {/* Emotion History */}
            {emotionHistory.length > 0 && (
              <div className="bg-white rounded-lg shadow-xl p-6 mt-6 fade-in">
                <h3 className="text-xl font-bold text-gray-800 mb-4">
                  üìä Recent Emotions
                </h3>
                <div className="space-y-3">
                  {emotionHistory.map((item, idx) => (
                    <div
                      key={idx}
                      className="flex justify-between items-center p-2 bg-gray-50 rounded"
                    >
                      <div>
                        <span className="text-2xl">
                          {emotionEmojis[item.emotion]}
                        </span>
                        <span className="ml-2 font-semibold text-gray-800 capitalize">
                          {item.emotion}
                        </span>
                      </div>
                      <div className="text-right">
                        <p className="text-xs text-gray-500">{item.timestamp}</p>
                        <p className="text-sm font-semibold text-blue-600">
                          {(item.confidence * 100).toFixed(0)}%
                        </p>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>

          {/* Right Content - Products */}
          <div className="md:col-span-2">
            <div className="bg-white rounded-lg shadow-xl p-6 fade-in">
              <h2 className="text-3xl font-bold mb-6 text-gray-800">
                üõçÔ∏è Recommended Products
              </h2>

              {/* Category Filter */}
              {detectedEmotion && (
                <CategoryFilter
                  categories={categories}
                  selectedCategory={selectedCategory}
                  onCategoryChange={handleCategoryChange}
                />
              )}

              {/* Products Grid */}
              <ProductGrid
                products={products}
                detectedEmotion={detectedEmotion}
                selectedCategory={selectedCategory}
              />
            </div>
          </div>
        </div>

        {/* Footer */}
        <div className="text-center mt-12 text-white opacity-75">
          <p className="text-sm">User ID: {userId}</p>
          <p className="text-xs mt-2">
            Backend: {API_URL}
          </p>
        </div>
      </div>
    </div>
  );
}

export default App;
