import React, { useRef, useState, useEffect } from 'react';
import Webcam from 'react-webcam';
import axios from 'axios';
import './App.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

// Configure axios with default headers
axios.interceptors.request.use(config => {
  const token = localStorage.getItem('access_token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

function App() {
  // Auth States
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [authMode, setAuthMode] = useState('login');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');
  const [authError, setAuthError] = useState(null);
  const [authLoading, setAuthLoading] = useState(false);
  const [userInfo, setUserInfo] = useState(null);

  // Emotion Detection States
  const webcamRef = useRef(null);
  const [capturedImage, setCapturedImage] = useState(null);
  const [detectedEmotion, setDetectedEmotion] = useState(null);
  const [emotionConfidence, setEmotionConfidence] = useState(null);
  const [products, setProducts] = useState([]);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [categories, setCategories] = useState(['All']);
  const [error, setError] = useState(null);

  // Shopping Cart & Wishlist States
  const [cart, setCart] = useState([]);
  const [wishlist, setWishlist] = useState([]);
  const [cartTotal, setCartTotal] = useState(0);
  const [showCart, setShowCart] = useState(false);
  const [showWishlist, setShowWishlist] = useState(false);

  const emotionEmojis = {
    happy: 'üòä',
    sad: 'üò¢',
    angry: 'üò†',
    neutral: 'üòê',
    surprise: 'üò≤',
    fear: 'üò®',
    disgust: 'ü§¢'
  };

  // Check auth on mount
  useEffect(() => {
    const token = localStorage.getItem('access_token');
    if (token) {
      setIsAuthenticated(true);
      fetchUserProfile();
      fetchCategories();
      fetchCart();
      fetchWishlist();
    }
  }, []);

  // ======================== AUTHENTICATION ========================

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      setAuthLoading(true);
      await axios.post(`${API_URL}/auth/register`, {
        email,
        password,
        name
      });
      alert('Registration successful! Please login.');
      setAuthMode('login');
      setEmail('');
      setPassword('');
      setName('');
    } catch (err) {
      setAuthError(err.response?.data?.error || 'Registration failed');
    } finally {
      setAuthLoading(false);
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      setAuthLoading(true);
      const response = await axios.post(`${API_URL}/auth/login`, {
        email,
        password
      });
      localStorage.setItem('access_token', response.data.access_token);
      setIsAuthenticated(true);
      setUserInfo(response.data.user);
      setEmail('');
      setPassword('');
      setAuthError(null);
      fetchUserProfile();
      fetchCategories();
      fetchCart();
      fetchWishlist();
    } catch (err) {
      setAuthError(err.response?.data?.error || 'Login failed');
    } finally {
      setAuthLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('access_token');
    setIsAuthenticated(false);
    setUserInfo(null);
    setCart([]);
    setWishlist([]);
  };

  // ======================== USER PROFILE ========================

  const fetchUserProfile = async () => {
    try {
      const response = await axios.get(`${API_URL}/auth/profile`);
      setUserInfo(response.data.user);
      console.log('Profile fetched:', response.data.user);
    } catch (err) {
      console.error('Error fetching profile:', err);
    }
  };

  // ======================== EMOTION DETECTION ========================

  const fetchCategories = async () => {
    try {
      const response = await axios.get(`${API_URL}/categories`);
      setCategories(response.data.categories || ['All']);
    } catch (err) {
      console.error('Error fetching categories:', err);
    }
  };

  const captureImage = () => {
    if (!webcamRef.current) {
      setError('Webcam not ready. Please allow camera access.');
      return;
    }
    try {
      const imageSrc = webcamRef.current.getScreenshot();
      if (!imageSrc) {
        setError('Failed to capture image.');
        return;
      }
      setCapturedImage(imageSrc);
      analyzeEmotion(imageSrc);
    } catch (err) {
      setError('Error capturing image: ' + err.message);
    }
  };

  const analyzeEmotion = async (base64Image) => {
    try {
      setIsAnalyzing(true);
      setError(null);
      console.log('Analyzing emotion...');
      
      const response = await axios.post(`${API_URL}/detect-emotion`, {
        image: base64Image
      });

      console.log('Response:', response.data);

      setDetectedEmotion(response.data.emotion);
      setEmotionConfidence(response.data.confidence);
      setProducts(response.data.products || []);
      setSelectedCategory('All');
      setIsAnalyzing(false);
    } catch (err) {
      console.error('Error:', err);
      setError(err.response?.data?.details || err.response?.data?.error || 'Failed to analyze emotion.');
      setIsAnalyzing(false);
    }
  };

  // ======================== SHOPPING CART ========================

  const fetchCart = async () => {
    try {
      const response = await axios.get(`${API_URL}/cart`);
      setCart(response.data.cart || []);
      setCartTotal(response.data.total_price || 0);
    } catch (err) {
      console.error('Error fetching cart:', err);
    }
  };

  const addToCart = async (product) => {
    try {
      await axios.post(`${API_URL}/cart/add`, {
        product_id: product.id,
        quantity: 1
      });
      alert('Added to cart!');
      fetchCart();
    } catch (err) {
      setError('Error adding to cart');
    }
  };

  const removeFromCart = async (productId) => {
    try {
      await axios.delete(`${API_URL}/cart/remove/${productId}`);
      fetchCart();
    } catch (err) {
      setError('Error removing from cart');
    }
  };

  // ======================== WISHLIST ========================

  const fetchWishlist = async () => {
    try {
      const response = await axios.get(`${API_URL}/wishlist`);
      setWishlist(response.data.wishlist || []);
    } catch (err) {
      console.error('Error fetching wishlist:', err);
    }
  };

  const addToWishlist = async (product) => {
    try {
      await axios.post(`${API_URL}/wishlist/add`, {
        product_id: product.id
      });
      alert('Added to wishlist!');
      fetchWishlist();
    } catch (err) {
      setError('Error adding to wishlist');
    }
  };

  // ======================== RENDER LOGIN ========================

  if (!isAuthenticated) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-500 via-purple-500 to-pink-500 p-4">
        <div className="max-w-md mx-auto">
          <div className="bg-white rounded-lg shadow-2xl p-8">
            <h1 className="text-3xl font-bold text-center mb-2">üé≠ Emotion Recommender</h1>
            <p className="text-center text-gray-600 mb-8">
              {authMode === 'login' ? 'Sign in' : 'Create account'}
            </p>

            {authError && (
              <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
                {authError}
              </div>
            )}

            <form onSubmit={authMode === 'login' ? handleLogin : handleRegister}>
              {authMode === 'register' && (
                <div className="mb-4">
                  <input
                    type="text"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    className="w-full px-4 py-2 border rounded-lg"
                    placeholder="Your name"
                    required
                  />
                </div>
              )}

              <div className="mb-4">
                <input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  className="w-full px-4 py-2 border rounded-lg"
                  placeholder="Email"
                  required
                />
              </div>

              <div className="mb-6">
                <input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="w-full px-4 py-2 border rounded-lg"
                  placeholder="Password"
                  required
                />
              </div>

              <button
                type="submit"
                disabled={authLoading}
                className="w-full py-3 bg-blue-600 text-white rounded-lg font-bold hover:bg-blue-700"
              >
                {authLoading ? 'Loading...' : authMode === 'login' ? 'Sign In' : 'Register'}
              </button>
            </form>

            <p className="text-center mt-4 text-gray-600">
              {authMode === 'login' ? "Don't have account? " : 'Have account? '}
              <button
                onClick={() => setAuthMode(authMode === 'login' ? 'register' : 'login')}
                className="text-blue-600 font-bold"
              >
                {authMode === 'login' ? 'Register' : 'Login'}
              </button>
            </p>
          </div>
        </div>
      </div>
    );
  }

  // ======================== RENDER MAIN APP ========================

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-500 via-purple-500 to-pink-500">
      <div className="bg-white shadow-lg p-4 mb-6">
        <div className="max-w-7xl mx-auto flex justify-between items-center">
          <h1 className="text-2xl font-bold">üé≠ Emotion Recommender</h1>
          <div className="flex gap-4">
            <button onClick={() => setShowCart(!showCart)} className="px-4 py-2 bg-blue-600 text-white rounded">
              üõí Cart ({cart.length})
            </button>
            <button onClick={() => setShowWishlist(!showWishlist)} className="px-4 py-2 bg-purple-600 text-white rounded">
              ‚ù§Ô∏è Wishlist ({wishlist.length})
            </button>
            <button onClick={handleLogout} className="px-4 py-2 bg-red-600 text-white rounded">
              Logout
            </button>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto p-4">
        {error && (
          <div className="mb-6 bg-red-100 border border-red-400 text-red-700 px-4 py-4 rounded">
            {error}
            <button onClick={() => setError(null)} className="float-right font-bold">√ó</button>
          </div>
        )}

        {/* Show Cart or Wishlist or Main */}
        {showCart ? (
          <div className="bg-white rounded-lg shadow-xl p-6">
            <h2 className="text-3xl font-bold mb-6">üõí Shopping Cart</h2>
            {cart && cart.length > 0 ? (
              <div>
                {cart.map((item, idx) => (
                  <div key={idx} className="flex justify-between p-4 bg-gray-50 rounded mb-2">
                    <div>
                      <h3 className="font-bold">{item.name}</h3>
                      <p>${item.price} x {item.quantity}</p>
                    </div>
                    <button onClick={() => removeFromCart(item.product_id)} className="text-red-600 font-bold">
                      Remove
                    </button>
                  </div>
                ))}
                <div className="mt-4 text-2xl font-bold">Total: ${cartTotal.toFixed(2)}</div>
              </div>
            ) : (
              <p className="text-gray-500">Cart is empty</p>
            )}
          </div>
        ) : showWishlist ? (
          <div className="bg-white rounded-lg shadow-xl p-6">
            <h2 className="text-3xl font-bold mb-6">‚ù§Ô∏è Wishlist</h2>
            {wishlist && wishlist.length > 0 ? (
              <div className="grid md:grid-cols-3 gap-4">
                {wishlist.map((item, idx) => (
                  <div key={idx} className="bg-gray-50 p-4 rounded">
                    <h3 className="font-bold">{item.name}</h3>
                    <p>{item.price}</p>
                  </div>
                ))}
              </div>
            ) : (
              <p className="text-gray-500">Wishlist is empty</p>
            )}
          </div>
        ) : (
          /* Main Emotion Detection */
          <div className="grid md:grid-cols-3 gap-6">
            <div className="md:col-span-1">
              <div className="bg-white rounded-lg shadow-xl p-6">
                <h2 className="text-2xl font-bold mb-4">üì∑ Capture Emotion</h2>

                {!detectedEmotion ? (
                  <>
                    <div className="bg-black rounded-lg overflow-hidden mb-4">
                      <Webcam
                        ref={webcamRef}
                        screenshotFormat="image/jpeg"
                        className="w-full"
                      />
                    </div>
                    <button
                      onClick={captureImage}
                      disabled={isAnalyzing}
                      className="w-full py-3 bg-blue-600 text-white rounded-lg font-bold hover:bg-blue-700"
                    >
                      {isAnalyzing ? '‚è≥ Analyzing...' : 'üéØ Capture & Analyze'}
                    </button>
                  </>
                ) : (
                  <>
                    <img src={capturedImage} alt="Captured" className="w-full rounded-lg mb-4" />
                    <div className="text-center mb-4 p-4 bg-blue-50 rounded">
                      <div className="text-6xl">{emotionEmojis[detectedEmotion]}</div>
                      <p className="text-2xl font-bold capitalize">{detectedEmotion}</p>
                      <p className="text-lg text-blue-600">{(emotionConfidence * 100).toFixed(0)}%</p>
                    </div>
                    <button
                      onClick={() => {
                        setDetectedEmotion(null);
                        setCapturedImage(null);
                        setProducts([]);
                      }}
                      className="w-full py-3 bg-purple-600 text-white rounded-lg font-bold"
                    >
                      üîÑ Detect Again
                    </button>
                  </>
                )}
              </div>
            </div>

            {/* Products */}
            <div className="md:col-span-2">
              <div className="bg-white rounded-lg shadow-xl p-6">
                <h2 className="text-3xl font-bold mb-6">üõçÔ∏è Recommended Products</h2>

                {detectedEmotion && (
                  <div className="mb-6 flex flex-wrap gap-2">
                    {categories.map((cat) => (
                      <button
                        key={cat}
                        onClick={() => setSelectedCategory(cat)}
                        className={`px-4 py-2 rounded ${selectedCategory === cat ? 'bg-blue-600 text-white' : 'bg-gray-200'}`}
                      >
                        {cat}
                      </button>
                    ))}
                  </div>
                )}

                {products && products.length > 0 ? (
                  <div className="grid md:grid-cols-2 gap-4">
                    {products
                      .filter(p => selectedCategory === 'All' || p.category === selectedCategory)
                      .map((product) => (
                        <div key={product.id} className="bg-gray-50 rounded-lg overflow-hidden shadow">
                          <img src={product.image} alt={product.name} className="w-full h-40 object-cover" />
                          <div className="p-4">
                            <h3 className="font-bold">{product.name}</h3>
                            <p className="text-blue-600 font-bold">${product.price}</p>
                            <button
                              onClick={() => addToCart(product)}
                              className="w-full py-2 bg-blue-600 text-white rounded mt-2 font-bold"
                            >
                              üõí Add to Cart
                            </button>
                            <button
                              onClick={() => addToWishlist(product)}
                              className="w-full py-2 bg-red-500 text-white rounded mt-1 font-bold"
                            >
                              ‚ù§Ô∏è Wishlist
                            </button>
                          </div>
                        </div>
                      ))}
                  </div>
                ) : detectedEmotion ? (
                  <p className="text-gray-500 text-center py-8">No products found</p>
                ) : (
                  <p className="text-gray-500 text-center py-8">Capture emotion to see products</p>
                )}
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
