import React, { useRef, useState, useEffect } from 'react';
import Webcam from 'react-webcam';
import axios from 'axios';
import './App.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

function App() {
  // Auth States
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [authMode, setAuthMode] = useState('login');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');
  const [authError, setAuthError] = useState(null);
  const [authLoading, setAuthLoading] = useState(false);
  const [userInfo, setUserInfo] = useState(null);

  // Emotion Detection States
  const webcamRef = useRef(null);
  const [capturedImage, setCapturedImage] = useState(null);
  const [detectedEmotion, setDetectedEmotion] = useState(null);
  const [emotionConfidence, setEmotionConfidence] = useState(null);
  const [products, setProducts] = useState([]);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [categories, setCategories] = useState(['All']);
  const [error, setError] = useState(null);

  // Shopping Cart & Wishlist States
  const [cart, setCart] = useState([]);
  const [wishlist, setWishlist] = useState([]);
  const [cartTotal, setCartTotal] = useState(0);
  const [showCart, setShowCart] = useState(false);
  const [showWishlist, setShowWishlist] = useState(false);

  // Product Reviews & Ratings
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [userRating, setUserRating] = useState(5);
  const [userComment, setUserComment] = useState('');
  const [showReviews, setShowReviews] = useState(false);

  // Order History
  const [orders, setOrders] = useState([]);
  const [showOrders, setShowOrders] = useState(false);

  // UI States
  const [currentPage, setCurrentPage] = useState('emotion');
  const [showProfile, setShowProfile] = useState(false);

  const emotionEmojis = {
    happy: 'ðŸ˜Š',
    sad: 'ðŸ˜¢',
    angry: 'ðŸ˜ ',
    neutral: 'ðŸ˜',
    surprise: 'ðŸ˜²',
    fear: 'ðŸ˜¨',
    disgust: 'ðŸ¤¢'
  };

  // Check auth on mount
  useEffect(() => {
    const token = localStorage.getItem('access_token');
    if (token) {
      setIsAuthenticated(true);
      fetchUserProfile();
      fetchCategories();
      fetchCart();
      fetchWishlist();
      fetchOrders();
    }
  }, []);

  // ======================== AUTHENTICATION ========================

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      setAuthLoading(true);
      const response = await axios.post(`${API_URL}/auth/register`, {
        email,
        password,
        name
      });
      alert('Registration successful! Please login.');
      setAuthMode('login');
      setEmail('');
      setPassword('');
      setName('');
    } catch (err) {
      setAuthError(err.response?.data?.error || 'Registration failed');
    } finally {
      setAuthLoading(false);
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      setAuthLoading(true);
      const response = await axios.post(`${API_URL}/auth/login`, {
        email,
        password
      });
      localStorage.setItem('access_token', response.data.access_token);
      setIsAuthenticated(true);
      setUserInfo(response.data.user);
      setEmail('');
      setPassword('');
      fetchUserProfile();
      fetchCategories();
      fetchCart();
      fetchWishlist();
    } catch (err) {
      setAuthError(err.response?.data?.error || 'Login failed');
    } finally {
      setAuthLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('access_token');
    setIsAuthenticated(false);
    setUserInfo(null);
    setCart([]);
    setWishlist([]);
    setDetectedEmotion(null);
    setProducts([]);
  };

  // ======================== USER PROFILE ========================

  const fetchUserProfile = async () => {
    try {
      const token = localStorage.getItem('access_token');
      const response = await axios.get(`${API_URL}/auth/profile`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUserInfo(response.data.user);
    } catch (err) {
      console.error('Error fetching profile:', err);
    }
  };

  // ======================== EMOTION DETECTION ========================

  const fetchCategories = async () => {
    try {
      const response = await axios.get(`${API_URL}/categories`);
      setCategories(response.data.categories || ['All']);
    } catch (err) {
      console.error('Error fetching categories:', err);
    }
  };

  const captureImage = () => {
    if (!webcamRef.current) {
      setError('Webcam not ready. Please allow camera access.');
      return;
    }
    try {
      const imageSrc = webcamRef.current.getScreenshot();
      if (!imageSrc) {
        setError('Failed to capture image.');
        return;
      }
      setCapturedImage(imageSrc);
      analyzeEmotion(imageSrc);
    } catch (err) {
      setError('Error capturing image: ' + err.message);
    }
  };

  const analyzeEmotion = async (base64Image) => {
    try {
      setIsAnalyzing(true);
      setError(null);
      const token = localStorage.getItem('access_token');
      const response = await axios.post(
        `${API_URL}/detect-emotion`,
        { image: base64Image },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setDetectedEmotion(response.data.emotion);
      setEmotionConfidence(response.data.confidence);
      setProducts(response.data.products || []);
      setSelectedCategory('All');
      setIsAnalyzing(false);
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to analyze emotion.');
      setIsAnalyzing(false);
    }
  };

  // ======================== SHOPPING CART ========================

  const fetchCart = async () => {
    try {
      const token = localStorage.getItem('access_token');
      const response = await axios.get(`${API_URL}/cart`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setCart(response.data.cart || []);
      setCartTotal(response.data.total_price || 0);
    } catch (err) {
      console.error('Error fetching cart:', err);
    }
  };

  const addToCart = async (product) => {
    try {
      const token = localStorage.getItem('access_token');
      await axios.post(
        `${API_URL}/cart/add`,
        {
          product_id: product.id,
          quantity: 1
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert('Product added to cart!');
      fetchCart();
    } catch (err) {
      setError('Error adding to cart');
    }
  };

  const removeFromCart = async (productId) => {
    try {
      const token = localStorage.getItem('access_token');
      await axios.delete(`${API_URL}/cart/remove/${productId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchCart();
    } catch (err) {
      setError('Error removing from cart');
    }
  };

  const clearCart = async () => {
    try {
      const token = localStorage.getItem('access_token');
      await axios.post(`${API_URL}/cart/clear`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchCart();
      alert('Cart cleared!');
    } catch (err) {
      setError('Error clearing cart');
    }
  };

  // ======================== WISHLIST ========================

  const fetchWishlist = async () => {
    try {
      const token = localStorage.getItem('access_token');
      const response = await axios.get(`${API_URL}/wishlist`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setWishlist(response.data.wishlist || []);
    } catch (err) {
      console.error('Error fetching wishlist:', err);
    }
  };

  const addToWishlist = async (product) => {
    try {
      const token = localStorage.getItem('access_token');
      await axios.post(
        `${API_URL}/wishlist/add`,
        { product_id: product.id },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert('Added to wishlist!');
      fetchWishlist();
    } catch (err) {
      setError('Error adding to wishlist');
    }
  };

  const removeFromWishlist = async (productId) => {
    try {
      const token = localStorage.getItem('access_token');
      await axios.delete(`${API_URL}/wishlist/remove/${productId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchWishlist();
    } catch (err) {
      setError('Error removing from wishlist');
    }
  };

  // ======================== REVIEWS & RATINGS ========================

  const fetchProductReviews = async (productId) => {
    try {
      const response = await axios.get(`${API_URL}/products/${productId}/reviews`);
      setReviews(response.data.reviews || []);
      setShowReviews(true);
    } catch (err) {
      console.error('Error fetching reviews:', err);
    }
  };

  const submitReview = async (productId) => {
    try {
      const token = localStorage.getItem('access_token');
      await axios.post(
        `${API_URL}/products/${productId}/reviews`,
        {
          rating: userRating,
          comment: userComment
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert('Review submitted!');
      fetchProductReviews(productId);
      setUserComment('');
      setUserRating(5);
    } catch (err) {
      setError('Error submitting review');
    }
  };

  // ======================== CHECKOUT & ORDERS ========================

  const handleCheckout = async () => {
    try {
      const token = localStorage.getItem('access_token');
      const response = await axios.post(
        `${API_URL}/checkout`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      
      if (response.data.session_url) {
        window.location.href = response.data.session_url;
      }
    } catch (err) {
      setError('Error during checkout. Please try again.');
    }
  };

  const fetchOrders = async () => {
    try {
      const token = localStorage.getItem('access_token');
      const response = await axios.get(`${API_URL}/orders`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setOrders(response.data.orders || []);
    } catch (err) {
      console.error('Error fetching orders:', err);
    }
  };

  // ======================== RENDER LOGIN PAGE ========================

  if (!isAuthenticated) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-500 via-purple-500 to-pink-500 p-4 md:p-8">
        <div className="max-w-md mx-auto">
          <div className="bg-white rounded-lg shadow-2xl p-8">
            <h1 className="text-3xl font-bold text-center mb-2 text-gray-800">
              ðŸŽ­ Emotion Recommender
            </h1>
            <p className="text-center text-gray-600 mb-8">
              {authMode === 'login' ? 'Sign in to your account' : 'Create a new account'}
            </p>

            {authError && (
              <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
                {authError}
              </div>
            )}

            <form onSubmit={authMode === 'login' ? handleLogin : handleRegister}>
              {authMode === 'register' && (
                <div className="mb-4">
                  <label className="block text-gray-700 font-semibold mb-2">Name</label>
                  <input
                    type="text"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:border-blue-500"
                    placeholder="Your name"
                    required
                  />
                </div>
              )}

              <div className="mb-4">
                <label className="block text-gray-700 font-semibold mb-2">Email</label>
                <input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:border-blue-500"
                  placeholder="your@email.com"
                  required
                />
              </div>

              <div className="mb-6">
                <label className="block text-gray-700 font-semibold mb-2">Password</label>
                <input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:border-blue-500"
                  placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"
                  required
                />
              </div>

              <button
                type="submit"
                disabled={authLoading}
                className="w-full py-3 bg-gradient-to-r from-blue-600 to-purple-600 text-white rounded-lg font-bold hover:from-blue-700 hover:to-purple-700 transition disabled:opacity-50"
              >
                {authLoading ? 'Loading...' : authMode === 'login' ? 'Sign In' : 'Register'}
              </button>
            </form>

            <p className="text-center mt-4 text-gray-600">
              {authMode === 'login' ? "Don't have an account?" : 'Already have an account?'}
              <button
                onClick={() => setAuthMode(authMode === 'login' ? 'register' : 'login')}
                className="ml-2 text-blue-600 font-semibold hover:underline"
              >
                {authMode === 'login' ? 'Register' : 'Sign In'}
              </button>
            </p>
          </div>
        </div>
      </div>
    );
  }

  // ======================== RENDER MAIN APP ========================

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-500 via-purple-500 to-pink-500">
      {/* Header */}
      <div className="bg-white shadow-lg">
        <div className="max-w-7xl mx-auto px-4 py-4 flex justify-between items-center">
          <h1 className="text-2xl font-bold text-blue-600">ðŸŽ­ Emotion Recommender</h1>
          
          <div className="flex gap-4 items-center">
            <button
              onClick={() => setCurrentPage('emotion')}
              className={`px-4 py-2 rounded ${currentPage === 'emotion' ? 'bg-blue-600 text-white' : 'bg-gray-200'}`}
            >
              ðŸŽ¯ Emotion
            </button>

            <button
              onClick={() => { setShowCart(!showCart); setCurrentPage('cart'); }}
              className="relative px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
            >
              ðŸ›’ Cart ({cart.length})
            </button>

            <button
              onClick={() => { setShowWishlist(!showWishlist); setCurrentPage('wishlist'); }}
              className="relative px-4 py-2 bg-purple-600 text-white rounded hover:bg-purple-700"
            >
              â¤ï¸ Wishlist ({wishlist.length})
            </button>

            <button
              onClick={() => { setShowOrders(!showOrders); setCurrentPage('orders'); }}
              className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700"
            >
              ðŸ“¦ Orders
            </button>

            <button
              onClick={() => setShowProfile(!showProfile)}
              className="px-4 py-2 bg-gray-600 text-white rounded hover:bg-gray-700"
            >
              ðŸ‘¤ Profile
            </button>

            <button
              onClick={handleLogout}
              className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700"
            >
              Logout
            </button>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto p-4 md:p-8">
        {/* Error Message */}
        {error && (
          <div className="mb-6 bg-red-100 border border-red-400 text-red-700 px-4 py-4 rounded-lg">
            <p>{error}</p>
            <button onClick={() => setError(null)} className="float-right font-bold">Ã—</button>
          </div>
        )}

        {/* User Profile */}
        {showProfile && userInfo && (
          <div className="bg-white rounded-lg shadow-xl p-6 mb-6">
            <h2 className="text-2xl font-bold mb-4">ðŸ‘¤ Your Profile</h2>
            <div className="grid md:grid-cols-2 gap-4">
              <div>
                <p className="text-gray-600">Email</p>
                <p className="text-xl font-bold">{userInfo.email}</p>
              </div>
              <div>
                <p className="text-gray-600">Name</p>
                <p className="text-xl font-bold">{userInfo.name}</p>
              </div>
              <div>
                <p className="text-gray-600">Member Since</p>
                <p className="text-xl font-bold">{new Date(userInfo.created_at).toLocaleDateString()}</p>
              </div>
              <div>
                <p className="text-gray-600">Orders</p>
                <p className="text-xl font-bold">{userInfo.orders_count}</p>
              </div>
            </div>
          </div>
        )}

        {/* Emotion Detection Section */}
        {currentPage === 'emotion' && (
          <div className="grid md:grid-cols-3 gap-6">
            {/* Webcam */}
            <div className="md:col-span-1">
              <div className="bg-white rounded-lg shadow-xl p-6">
                <h2 className="text-2xl font-bold mb-4 text-gray-800">ðŸ“· Capture Emotion</h2>

                {!detectedEmotion ? (
                  <>
                    <div className="bg-black rounded-lg overflow-hidden mb-4">
                      <Webcam
                        ref={webcamRef}
                        screenshotFormat="image/jpeg"
                        className="w-full"
                        videoConstraints={{
                          width: { ideal: 640 },
                          height: { ideal: 480 },
                          facingMode: 'user'
                        }}
                      />
                    </div>

                    <button
                      onClick={captureImage}
                      disabled={isAnalyzing}
                      className={`w-full py-3 rounded-lg font-bold text-white text-lg transition ${
                        isAnalyzing
                          ? 'bg-gray-400'
                          : 'bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700'
                      }`}
                    >
                      {isAnalyzing ? 'â³ Analyzing...' : 'ðŸŽ¯ Capture & Analyze'}
                    </button>
                  </>
                ) : (
                  <>
                    <div className="bg-gray-100 rounded-lg overflow-hidden mb-4">
                      <img src={capturedImage} alt="Captured" className="w-full" />
                    </div>

                    <div className="text-center mb-6 p-4 bg-gradient-to-r from-blue-50 to-purple-50 rounded-lg">
                      <div className="text-6xl mb-2">{emotionEmojis[detectedEmotion] || 'ðŸ˜'}</div>
                      <p className="text-2xl font-bold text-gray-800 capitalize">{detectedEmotion}</p>
                      <p className="text-lg font-semibold text-blue-600">{(emotionConfidence * 100).toFixed(0)}%</p>
                    </div>

                    <button
                      onClick={() => {
                        setDetectedEmotion(null);
                        setEmotionConfidence(null);
                        setCapturedImage(null);
                        setProducts([]);
                      }}
                      className="w-full py-3 rounded-lg font-bold text-white bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 transition"
                    >
                      ðŸ”„ Detect Again
                    </button>
                  </>
                )}
              </div>
            </div>

            {/* Products */}
            <div className="md:col-span-2">
              <div className="bg-white rounded-lg shadow-xl p-6">
                <h2 className="text-3xl font-bold mb-6 text-gray-800">ðŸ›ï¸ Recommended Products</h2>

                {detectedEmotion && categories.length > 0 && (
                  <div className="mb-6 p-4 bg-gray-50 rounded-lg">
                    <label className="block text-sm font-bold text-gray-700 mb-3">Filter:</label>
                    <div className="flex flex-wrap gap-2">
                      {categories.map((cat) => (
                        <button
                          key={cat}
                          onClick={() => setSelectedCategory(cat)}
                          className={`px-4 py-2 rounded-full font-semibold ${
                            selectedCategory === cat
                              ? 'bg-blue-600 text-white'
                              : 'bg-gray-200 text-gray-800'
                          }`}
                        >
                          {cat}
                        </button>
                      ))}
                    </div>
                  </div>
                )}

                {products && products.length > 0 ? (
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {products
                      .filter(p => selectedCategory === 'All' || p.category === selectedCategory)
                      .map((product) => (
                        <div key={product.id} className="bg-gray-50 rounded-lg overflow-hidden shadow hover:shadow-lg transition">
                          <div className="relative h-40 bg-gray-300">
                            <img
                              src={product.image}
                              alt={product.name}
                              className="w-full h-full object-cover"
                              onError={(e) => {
                                e.target.src = 'https://via.placeholder.com/400x300?text=Product';
                              }}
                            />
                          </div>
                          <div className="p-4">
                            <h3 className="font-bold text-gray-800 mb-2 line-clamp-2">{product.name}</h3>
                            <div className="flex justify-between items-start gap-2 mb-3">
                              <span className="text-2xl font-bold text-blue-600">${product.price}</span>
                              <span className="text-xs bg-purple-100 text-purple-800 px-2 py-1 rounded">
                                {product.category}
                              </span>
                            </div>

                            {product.rating > 0 && (
                              <p className="text-xs text-gray-600 mb-2">
                                â­ {product.rating.toFixed(1)} ({product.reviews_count} reviews)
                              </p>
                            )}

                            <div className="space-y-2">
                              <button
                                onClick={() => addToCart(product)}
                                className="w-full py-2 bg-blue-600 text-white rounded-lg font-semibold hover:bg-blue-700"
                              >
                                ðŸ›’ Add to Cart
                              </button>
                              <div className="flex gap-2">
                                <button
                                  onClick={() => addToWishlist(product)}
                                  className="flex-1 py-2 bg-red-500 text-white rounded-lg font-semibold hover:bg-red-600"
                                >
                                  â¤ï¸ Wishlist
                                </button>
                                <button
                                  onClick={() => {
                                    setSelectedProduct(product);
                                    fetchProductReviews(product.id);
                                  }}
                                  className="flex-1 py-2 bg-green-500 text-white rounded-lg font-semibold hover:bg-green-600"
                                >
                                  â­ Reviews
                                </button>
                              </div>
                            </div>
                          </div>
                        </div>
                      ))}
                  </div>
                ) : detectedEmotion ? (
                  <div className="text-center py-12 text-gray-500">
                    <p className="text-4xl mb-4">ðŸ˜…</p>
                    <p className="text-lg">No products found for this category.</p>
                  </div>
                ) : (
                  <div className="text-center py-16 text-gray-500">
                    <p className="text-4xl mb-4">ðŸ‘ˆ</p>
                    <p className="text-lg font-semibold">Capture your emotion to see recommendations</p>
                  </div>
                )}
              </div>
            </div>
          </div>
        )}

        {/* Shopping Cart */}
        {currentPage === 'cart' && (
          <div className="bg-white rounded-lg shadow-xl p-6">
            <h2 className="text-3xl font-bold mb-6">ðŸ›’ Shopping Cart</h2>
            {cart && cart.length > 0 ? (
              <>
                <div className="space-y-4 mb-6">
                  {cart.map((item, idx) => (
                    <div key={idx} className="flex justify-between items-center bg-gray-50 p-4 rounded-lg">
                      <div className="flex-1">
                        <h3 className="font-bold">{item.name}</h3>
                        <p className="text-gray-600">${item.price} x {item.quantity}</p>
                      </div>
                      <div className="text-right">
                        <p className="font-bold text-lg">${(item.price * item.quantity).toFixed(2)}</p>
                        <button
                          onClick={() => removeFromCart(item.product_id)}
                          className="text-red-600 font-bold hover:underline"
                        >
                          Remove
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
                <div className="border-t pt-4">
                  <p className="text-2xl font-bold mb-4">Total: ${cartTotal.toFixed(2)}</p>
                  <div className="flex gap-4">
                    <button
                      onClick={clearCart}
                      className="flex-1 py-3 bg-red-600 text-white rounded-lg font-bold hover:bg-red-700"
                    >
                      Clear Cart
                    </button>
                    <button
                      onClick={handleCheckout}
                      className="flex-1 py-3 bg-green-600 text-white rounded-lg font-bold hover:bg-green-700"
                    >
                      ðŸ’³ Checkout with Stripe
                    </button>
                  </div>
                </div>
              </>
            ) : (
              <p className="text-gray-500 text-center py-8">Your cart is empty</p>
            )}
          </div>
        )}

        {/* Wishlist */}
        {currentPage === 'wishlist' && (
          <div className="bg-white rounded-lg shadow-xl p-6">
            <h2 className="text-3xl font-bold mb-6">â¤ï¸ Wishlist</h2>
            {wishlist && wishlist.length > 0 ? (
              <div className="grid md:grid-cols-3 gap-4">
                {wishlist.map((item, idx) => (
                  <div key={idx} className="bg-gray-50 rounded-lg overflow-hidden shadow">
                    <img src={item.image} alt={item.name} className="w-full h-40 object-cover" />
                    <div className="p-4">
                      <h3 className="font-bold mb-2">{item.name}</h3>
                      <p className="font-bold text-blue-600 mb-3">{item.price}</p>
                      <button
                        onClick={() => removeFromWishlist(item.product_id)}
                        className="w-full py-2 bg-red-600 text-white rounded-lg font-bold hover:bg-red-700"
                      >
                        Remove from Wishlist
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <p className="text-gray-500 text-center py-8">Your wishlist is empty</p>
            )}
          </div>
        )}

        {/* Orders */}
        {currentPage === 'orders' && (
          <div className="bg-white rounded-lg shadow-xl p-6">
            <h2 className="text-3xl font-bold mb-6">ðŸ“¦ Order History</h2>
            {orders && orders.length > 0 ? (
              <div className="space-y-4">
                {orders.map((order) => (
                  <div key={order._id} className="bg-gray-50 p-4 rounded-lg border-l-4 border-blue-600">
                    <div className="flex justify-between items-start mb-2">
                      <h3 className="font-bold">Order {order._id.substring(0, 8)}</h3>
                      <span className="bg-green-100 text-green-800 px-3 py-1 rounded-full text-sm font-bold">
                        {order.status}
                      </span>
                    </div>
                    <p className="text-gray-600 text-sm mb-2">
                      {new Date(order.created_at).toLocaleDateString()}
                    </p>
                    <p className="font-bold text-lg text-blue-600 mb-2">Total: ${order.total_price}</p>
                    <div className="text-sm">
                      <p className="text-gray-600">Items: {order.items.length}</p>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <p className="text-gray-500 text-center py-8">No orders yet</p>
            )}
          </div>
        )}

        {/* Product Reviews Modal */}
        {showReviews && selectedProduct && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4">
            <div className="bg-white rounded-lg p-6 max-w-2xl w-full max-h-96 overflow-y-auto">
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-2xl font-bold">â­ Reviews for {selectedProduct.name}</h2>
                <button
                  onClick={() => setShowReviews(false)}
                  className="text-3xl font-bold text-gray-400 hover:text-gray-600"
                >
                  Ã—
                </button>
              </div>

              {/* Add Review Form */}
              <div className="mb-6 p-4 bg-gray-50 rounded-lg">
                <h3 className="font-bold mb-3">Add Your Review</h3>
                <div className="mb-3">
                  <label className="block text-sm font-bold mb-1">Rating</label>
                  <select
                    value={userRating}
                    onChange={(e) => setUserRating(parseInt(e.target.value))}
                    className="w-full px-3 py-2 border rounded-lg"
                  >
                    {[5, 4, 3, 2, 1].map(r => (
                      <option key={r} value={r}>{r} â­</option>
                    ))}
                  </select>
                </div>
                <div className="mb-3">
                  <label className="block text-sm font-bold mb-1">Comment</label>
                  <textarea
                    value={userComment}
                    onChange={(e) => setUserComment(e.target.value)}
                    className="w-full px-3 py-2 border rounded-lg"
                    rows="3"
                    placeholder="Share your thoughts..."
                  ></textarea>
                </div>
                <button
                  onClick={() => submitReview(selectedProduct.id)}
                  className="w-full py-2 bg-blue-600 text-white rounded-lg font-bold hover:bg-blue-700"
                >
                  Submit Review
                </button>
              </div>

              {/* Reviews List */}
              <div className="space-y-3">
                {reviews && reviews.length > 0 ? (
                  reviews.map((review, idx) => (
                    <div key={idx} className="p-3 bg-gray-50 rounded-lg">
                      <div className="flex justify-between mb-1">
                        <p className="font-bold">{review.username}</p>
                        <p>{'â­'.repeat(review.rating)}</p>
                      </div>
                      <p className="text-gray-700 text-sm">{review.comment}</p>
                    </div>
                  ))
                ) : (
                  <p className="text-gray-500 text-center">No reviews yet</p>
                )}
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
